import React, { Component } from "react";
import { render } from "react-dom";
import { Link } from "react-router-dom";

import userService from "../../service/user.service";

import loginImage from "../../assets/images/login-image.png";
import { Redirect } from "react-router-dom";
import { config } from "../../config/config";

const APIURL = "http://localhost:3000/api"; //config.apiUrl;

class LoginComponent extends Component {
  state = {
    username: "",
    password: "",
    isSubmitted: false,
  };

  constructor(props) {
    //super(props);

    //console.log('about to reset login status');
    // reset login status
    //this.props.logout();
    console.log("reset login status");

    this.state = {
      username: "",
      password: "",
      isSubmitted: false,
    };

    this.handleChange = this.handleChange.bind(this);
    this.login = this.login.bind(this);
  }

  handleChange(e) {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  }

  login() {
    this.state.isSubmitted = true;
    const { username, password } = this.state;
    if (username && password) {
      console.log(`username ${username} and  ${password} logged in`);
      userService.login(username, password).then(
        (user) => {
          console.log(user);
          return <Redirect to={APIURL + "/index"} />;
        },
        (error) => {
          console.log(error);
          <Redirect to={APIURL + "/login"} />;
        }
      );
    }
  }

  render() {
    const { username, password, isSubmitted } = this.state;

    return (
      <div className="login-page">
        <h2> Login </h2>
        <div>
          <img className="loginImg" src={loginImage}></img>
          <form className="loginForm" onSubmit={this.login}>
            <div className="form-group">
              <label htmlFor="username">Username: </label>
              <input
                type="text"
                placeholder="Username"
                name="username"
                value={username}
                onChange={this.handleChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password: </label>
              <input
                type="password"
                placeholder="Password"
                name="password"
                value={password}
                onChange={this.handleChange}
              />
            </div>
            <div className="form-group-btns">
              <button className="btn btn-primary">Login</button>
              <Link to="/register" className="btn btn-link">
                Register
              </Link>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default LoginComponent;
