import React, { Component } from "react";
import Car from "../car/Car";
import carService from "../../service/car.service";
import config from "../../config/config";

const APIURL = config.apiUrl;

class ViewCarsComponent extends Component {
  state = {
    cars: [],
  };

  componentDidMount() {
    this.retrieveCars();
  }

  retrieveCars() {
    fetch(APIURL + "/getCars", {
      method: "GET",
    })
      .then((res) => res.json())
      .then((json) => this.setState({ cars: json.cars }))
      .catch((err) => {
        console.log("err is " + err.message);
      });
  }

  updateCar(carId) {
    carService.updateCar(carId);
  }

  render() {
    const { cars } = this.props;
    return (
      <div className="App">
        <div>Welcome to Rent a car online system! </div>

        <br />
        <br />
        <h3> Cars available for rent: </h3>

        <div>
          {this.state.cars.map((car) => {
            return <Car key={car._id} car={car} />;
          })}
        </div>
        <div className="UdpateButton">
          <button
            onClick={this.updateCar(this.state.cars.map((car) => car._id))}
          >
            Update
          </button>
        </div>
      </div>
    );
  }
}

export default ViewCarsComponent;
